import Vue from 'vue';
import App from './App.vue';
import VueRouter from 'vue-router';

Vue.config.productionTip = false;

// Import your VueRouter instance and routes configuration
import DisplayData from './components/DisplayData.vue'; // assuming you have a DisplayData component

Vue.use(VueRouter);

const routes = [
  { path: '/', component: App }, // Assuming your App component is your main component
  { path: '/display-data', component: DisplayData }
];

const router = new VueRouter({
  mode: 'history', // Use HTML5 History API for clean URLs
  routes
});

new Vue({
  router,
  render: h => h(App),
}).$mount('#app');
